import styled from 'styled-components';

export const StyledStage = styled.div`
    display: grid;
    grid-gap: 1px;
    border: 2px solid #333;
    grid-template-columns: repeat(${props => props.width}, 1fr);
    width: 100%;
    max-width: 25vw;
    background: #111;
    width: 100%;
`

// ask for help for grid-template-rows
