import styled from 'styled-components';

export const StyledStage = styled.div`
    display: grid;
    grid-gap: 1px;
    border: 2px solid #333;
    grid-template-columns: repeat(10, 1fr);
    width: 100%;
    max-width: 25vw;
    background: #111;
`

// ask for help for grid-template-rows et grid-template-columns
